INSERT INTO `ontologies` (`id`, `ask`, `answer`, `created_at`, `updated_at`) VALUES
(1, 'bán gì', '[\"Đây là sàn thương mại điện tử, nơi các nhà bán hàng bán các sản phẩm đa dạng khác nhau như là: điện thoại, quần áo,...\"]', NULL, NULL),
(2, 'Mua hàng, đặt hàng', '[\"Khi cần mua hàng, bạn nhấn vào biểu tượng giỏ hàng → Chọn sản phẩm cần mua → Ấn thanh toán → Điền các thông tin về địa chỉ giao hàng và chọn phương thức thanh toán → Ấn đặt hàng. \"]', NULL, NULL),
(3, 'Chăm sóc, hỗ trợ', '[\"Nếu có bất kỳ vấn đề gì cần trao đổi quý khách có thể liên hệ hotline 0937 435 006. Chân thành cảm ơn quý khách!\"]', NULL, NULL),
(4, 'Đánh giá, bình luận', '[\"Bạn có thể đánh giá sao và bình luận về sản phẩm bạn đã mua bằng cách ấn vào sản phẩm muốn đánh giá → chọn mục review → Nhập đánh giá và ấn nút gửi.\"]', NULL, NULL),
(5, 'Nâng cấp thành nhà bán hàng, muốn bán hàng', '[\"Để đăng ký nhà bán hàng, bạn nhấp vào tên của tài khoản → Đăng ký bán hàng  Nhập các thông tin đăng ký → ấn nút gửi.\"]', NULL, NULL),
(6, 'Thanh toán sản phẩm', '[\"Có 2 hình thức thanh toán là: Thanh toán khi nhận hàng (COD) và Thanh toán online.\"]', NULL, NULL),
(7, 'tiền mặt, COD, thanh toán khi nhận hàng', '[\"Cửa hàng có hỗ trợ hình thức thanh toán khi nhận hàng\"]', NULL, NULL),
(8, 'online, thẻ, ngân hàng', '[\"Bạn có thể thanh toán bằng thẻ của các ngân hàng như Agribank, Vietcombank, Vietinbank, ACB, VISA/MASTER, SacomBank, Techcombank,…\"]', NULL, NULL),
(9, 'Tạo tài khoản, đăng ký', '[\"Khi cần đăng ký tài khoản, bạn nhấn vào biểu tượng đăng ký và điền các thông tin để đăng ký tài khoản → Cửa hàng sẽ gửi mail đến địa chỉ bạn đã đăng ký, khi bạn ấn xác nhận sẽ đăng ký tài khoản thành công.\"]', NULL, NULL),
(10, 'đăng nhập', '[\"Bạn nhấn vào biểu tượng đăng nhập → Điền Email và password để đăng nhập. Bạn cũng có thể đăng nhập bằng tài khoản Facebook hoặc Google.\"]', NULL, NULL),
(11, 'quên mật khẩu', '[\"Bạn nhấn vào biểu tượng đăng nhập → Chọn quên mật khẩu → Điền Email để lấy lại mật khẩu → Cửa hàng sẽ gửi mail đến địa để bạn ấn xác nhận và đặt lại mật khẩu mới.\"]', NULL, NULL),
(12, 'đổi mật khẩu', '[\"Để đổi mật khẩu, bạn nhấp vào tên của tài khoản → Thông tin tài khoản → Đổi mật khẩu.\"]', NULL, NULL),
(13, 'tình trạng đơn hàng', '[\"Sau khi mua hàng, bạn có thể theo dõi tình trạng đơn hàng bằng cách nhấn vào biểu tượng giỏ hàng → Chọn tab tình trạng đơn hàng\"]', NULL, NULL),
(14, 'tìm kiếm', '[\"Bạn nhập từ khóa tìm kiếm trên thanh tìm kiếm.Ngoài ra quý khách có thể tìm kiếm theo loại sản phẩm.\"]', NULL, NULL),
(15, 'thêm sản phẩm', '[\"Bạn chọn sản phẩm cần thêm và ấn vào chi tiết sản phẩm → Chọn số lượng (và các thông tin khách,..) → Chọn thêm vào giỏ hàng.\"]', NULL, NULL),
(16, 'cập nhật sản phẩm, sửa sản phẩm, chỉnh sản phẩm', '[\"Bạn nhấn vào biểu tượng giỏ hàng → Chọn sản phẩm cần chỉnh số lượng → Chỉnh số lượng.\"]', NULL, NULL),
(17, 'xóa sản phẩm', '[\"Bạn nhấn vào biểu tượng giỏ hàng → Chọn sản phẩm cần xóa → Ấn nút xóa.\"]', NULL, NULL),
(18, 'loại sản phẩm, loại hàng', '[\"Cửa hàng bao gồm các loại hàng như quần áo, thiết bị điện tử, mỹ phẩm, đồ gia dụng, bách hóa,… Chọn loại sản phẩm cần xem để xem tất cả các sản phẩm thuộc loại sản phẩm đó\"]', NULL, NULL),
(19, 'nhắn tin, trò chuyện, trao đổi', '[\"Bạn chọn biểu tượng tin nhắn → Chọn người cần nhắn và nhắn tin.\"]', NULL, NULL),
(20, 'cập nhật thông tin, sửa sản thông tin, chỉnh thông tin, cập nhật tài khoản, sửa sản tài khoản, chỉnh tài khoản', '[\"Để cập nhật thông tin tài khoản, bạn nhấp vào tên của tài khoản → Thông tin tài khoản → Chọn thông tin cần cập nhật → Ấn xác nhận.\"]', NULL, NULL);
